INSERT INTO `institucional` (`id`, `titulo_pt`, `conteudo_pt`, `titulo_en`, `conteudo_en`, `visivel`, `ordem`, `status`) VALUES
(1, ' Escritório', '<p style="text-align: justify;" data-mce-style="text-align: justify;">Atuante no ramo da Arquitetura e Urbanismo, a <strong>Vanessa Guerra Arquitetura</strong> é uma empresa jovem que, desde 2006, atua na elaboração de Projetos Completos de Arquitetura e Interiores, tendo como destaque a especialização em empreendimentos comerciais, corporativos e de serviços.</p><p style="text-align: justify;" data-mce-style="text-align: justify;">A nossa atuação começa antes mesmo da elaboração do projeto propriamente dito, assessorando os clientes na detecção de suas necessidades e carências, desde a escolha do terreno e/ou ponto apropriado para o seu negócio até a supervisão dos serviços preliminares de planejamento.</p><p style="text-align: justify;" data-mce-style="text-align: justify;">Além dos projetos de Arquitetura e Interiores, os nossos profissionais são habilitados para orientar, coordenar e compatibilizar todas as demais disciplinas complementares necessárias para a execução das obras, tais como projetos de instalações prediais – elétrica e hidráulica e redes de dados e voz. Além disso, temos o comprometimento em gerenciar, fiscalizar e acompanhar a execução das obras.</p><p style="text-align: justify;" data-mce-style="text-align: justify;"><br></p><h2 style="text-align: justify;" data-mce-style="text-align: justify;">Missão</h2><p style="text-align: justify;" data-mce-style="text-align: justify;">Nossa missão é elaborar projetos que resultem em obras de qualidade e estética original, cujo impacto seja positivo sobre o ambiente, a paisagem e a sociedade.</p><p style="text-align: justify;" data-mce-style="text-align: justify;"><br></p><h2 style="text-align: justify;" data-mce-style="text-align: justify;">Meta</h2><p style="text-align: justify;" data-mce-style="text-align: justify;">Nossa meta é atender a expectativa de nossos clientes através de um entendimento afinado entre arquitetos e clientes, onde as soluções propostas não seja resultado de uma impressão pessoal e sim do melhor resultado para as necessidades apresentadas.</p><p style="text-align: justify;" data-mce-style="text-align: justify;"><br></p>', '', '', '1', 1, '1'),
(2, 'Equipe', '<p><strong>Vanessa Guerra</strong><br>Arquiteta e Urbanista - Sócia Fundadora<br>Formada pelo Curso de Arquitetura e Urbanismo da Universidade do Vale do Rio dos Sinos – UNISINOS. Com uma vasta experiência na elaboração de projetos de Arquitetura e Interiores de diversos portes e especialidades, é a responsável pela concepção dos projetos e pela gestão da equipe técnica.<br></p><p><br><strong>Daniela Fontanari</strong><br>Acadêmica no 4º ano do curso de Arquitetura e Urbanismo pela Universidade do Vale do Rio dos Sinos – UNISINOS. Ingressou no escritório em 2008 e desde então atua como coordenadora técnica do escritório. É responsável pelo detalhamento técnico dos projetos desenvolvidos e administra todo o processo interno.<br></p><p><br><strong>Isabelle Carolina Mangoni Soares</strong><br>Acadêmica do 2º ano do curso de Arquitetura pela Universidade do Vale do Rio dos Sinos - UNISINOS. No escritório é responsável por detalhamentos técnicos de projetos. Faz parte da equipe desde 2012.<br></p><p><br><strong>Bruna Lauxem Costa</strong><br>Acadêmica do 2º ano do curso de Arquitetura pela Universidade do Vale do Rio dos Sinos - UNISINOS. No escritório é responsável pela gestão administrativa dos projetos; pelo atendimento e relacionamento com cliente. Faz parte da equipe desde 2012.<br></p><p><br><strong>Marlo Lauter Junior</strong><br>Designer gráfico responsável por ilustrar as ideias de cada criação através de maquetes eletrônicas. Faz parte da equipe desde 2010.</p>', '', '', '1', 2, '1'),
(3, 'teste2', '<p>test2<br data-mce-bogus="1"></p>', '', '', '1', 3, '9');
